  def "Third Maximum Number approach 1"() {

    given:
    ThirdMaximumNumber thirdMaximumNumber = new ThirdMaximumNumber();

    when:
    def arr = createArray(nums)
    int num = thirdMaximumNumber.thirdMaxAproach1(arr)

    then:
    num == numExpected

