  def "Third Maximum Number"() {

    given:
    ThirdMaximumNumber thirdMaximumNumber = new ThirdMaximumNumber();

    when:
    def arr = createArray(nums)
    int num = thirdMaximumNumber.thirdMaxApproach1(arr)

    then:
    num == numExpected

    where:
    nums    | numExpected
    "3,2,1" | 1
    "4,555" | 555
    "10"    | 10

  }

  def "Find All Numbers Disappeared in an Array too slow"() {
    given:
    NumbersDissapeared numbersDissapeared = new NumbersDissapeared();

    when:
    def arr = createArray(nums)
    List<Integer> res = numbersDissapeared.findDisappearedNumbers(arr);

    then:
    res.size() == length

    where:
    nums              | length
    "4,3,2,7,8,2,3,1" | 2

  }

  def "Find All Numbers Disappeared in an Array optimal"() {
    given:
    NumbersDissapeared numbersDissapeared = new NumbersDissapeared();

    when:
    def arr = createArray(nums)
    List<Integer> res = numbersDissapeared.findDisappearedNumbersOptimal(arr)

    then:
    res.size() == length

    where:
    nums              | length
    "4,3,2,7,8,2,3,1" | 2
  }

  def "Find Pivot Index"() {
    given:
    FindPivot findPivot = new FindPivot();

    when:
    int pivot = findPivot.pivotIndex(createArray(nums))

    then:
    pivot == pivotExpected

    where:
    nums             | pivotExpected
    "1,2,3,8,0,0,6"  | 3
    "1"              | -1
    "1,2"            | -1
    "1,2,1"          | 1
    "1,2,0"          | -1
    "1,7,3,6,5,6"    | 3
    "-1,-1,-1,0,1,1" | 0
  }

  def createArray(String val) {
    String[] n = val.split(",")
    def A = new int[n.length]
    for (int i = 0; i < n.length; i++) {
      A[i] = Integer.parseInt(n[i])
    }
    return A
  }

}
